import { Client, Message } from 'discord.js';

declare module "handler.djs" {
  class Application {
    constructor({client: Client, commandsPath: string, EventsPath: String, validationPath: string, owners: Array });
    setPrefix(prefix: String): any;
    setCooldown({message: String, reference: boolean, long: Boolean, Mdelete: string });
    build(): any;
    setDate(data: Object): any;
    getCommand(name: String): any;
    sendTimedMessage(message: Message, options: Message, reference: Boolean, timer: Number);
  }
}